import React, {useEffect, useState} from 'react';
import classes from './Projets.module.css';
import Title from './Title';
import CardProjet from './CardProjet';
import foodapi from '../assets/images/foodapi.png'
import todolist from '../assets/images/todolist.png'
import quizz from '../assets/images/quizz.png'
const Projets=()=> {
    const [allProjects, setAllProjects]=useState([])
    useEffect(()=>{
        fetch('http://localhost:4000/projects/allprojects')
        .then((response)=>response.json())
        .then((data)=>setAllProjects(data))
    },[])


    return (
        <div className={classes.projets} id="projects">
            <Title title="Projets"/>
            <div className={classes.list_projets}>

                {allProjects.map((oneProject)=>(
                    <CardProjet key={oneProject._id} title={oneProject.title} img={oneProject.image} link={oneProject.link} text={oneProject.texte}/>
                ))}
                {/* <CardProjet title="Food API" img={foodapi} linkGit="https://github.com/anibal-JUAREZ/portfolio/tree/main/FOOD%20API"/>
                <CardProjet title="To-Do List" img={todolist} linkGit="https://github.com/anibal-JUAREZ/portfolio/tree/main/TO-DO-LIST"/>
                <CardProjet title="Quiz" img={quizz} linkGit="https://github.com/anibal-JUAREZ/portfolio/tree/main/QUIZZ-PORTFOLIO"/> */}
            </div>

        </div>
    );
}

export default Projets;
