import React, {useState} from 'react';
import {Link} from 'react-router-dom';
import classes from './NavBar.module.css';
const NavBar=()=> {

    const [active, setActive] = useState("nav__menu");
    const navToggle = () =>{
        active==="nav__menu" ? setActive("nav__menu classes.nav__active") : setActive("nav__menu");    }
    return (


           <nav className={classes.nav}>

                <Link to='/' className={classes.nav__brand}>ajuarez</Link>
                <ul className={`classes.${active}`}>
                    <li className={classes.nav__item}>
                        <Link to='/' className={classes.nav__link}>accueil</Link>
                    </li>
                    <li className={classes.nav__item}>
                        <Link to='/aboutme' className={classes.nav__link}>a propos de moi</Link>
                    </li>
                    <li className={classes.nav__item}>
                        <Link to='/project' className={classes.nav__link}>projets</Link>
                    </li>
                    <li className={classes.nav__item}>
                        <Link to='/contact' className={classes.nav__link}>contact</Link>
                    </li>
                </ul>
                <div onClick = {navToggle} className={classes.nav__toggler}>
                    <div className={classes.line1}></div>
                    <div className={classes.line2}></div>
                    <div className={classes.line3}></div>

                </div>








           </nav>


    );
}

export default NavBar;
